import React, { useState, useEffect } from "react";
import ChatBoxRenderer from "./chat-box-renderer";
import Image from "../image";

const ChatBoxContainer = ({ socket, room, username }) => {
  const [currentMsg, setCurrentMsg] = useState("");
  const [msgList, setMsgList] = useState([]);
  const [file, setFile] = useState();

  const sendMsg = async () => {
    if (file) {
      if (currentMsg !== "") {
        const msgData = {
          type: "file",
          mimeType: file.type,
          fileName: file.name,
          body: file,
          room: room,
          author: username,
          msg: currentMsg,
          time:
            new Date(Date.now()).getHours() +
            ":" +
            new Date(Date.now()).getMinutes(),
        };
        await socket.emit("send_msg", msgData);
        setMsgList((list) => [...list, msgData]);
        setCurrentmsg("");
        setFile();
      }
    } else {
      if (currentmsg !== "") {
        const msgData = {
          type: "text",
          room: room,
          author: username,
          msg: currentmsg,
          time:
            new Date(Date.now()).getHours() +
            ":" +
            new Date(Date.now()).getMinutes(),
        };
        await socket.emit("send_msg", msgData);
        setMsgList((list) => [...list, msgData]);
        setCurrentMsg("");
      }
    }
  };

  useEffect(() => {
    socket.on("receive_msg", (data) => {
      setMsgList((list) => [...list, data]);
    });
  }, [socket]);

  const [bold, setBold] = useState(true);
  const [italic, setItalic] = useState(true);
  const [strike, setStrike] = useState(true);

  const addBold = () => {
    document.getElementById("innerText").classList.add("bold");
    document.getElementById("bold").classList.add("white-color");

    setBold(false);
  };

  const removeBold = () => {
    document.getElementById("innerText").classList.remove("bold");
    document.getElementById("bold").classList.remove("white-color");

    setBold(true);
  };
  const addItalic = () => {
    document.getElementById("innerText").classList.add("italic");
    document.getElementById("italic").classList.add("white-color");
    setItalic(false);
  };

  const removeItalic = () => {
    document.getElementById("innerText").classList.remove("italic");
    document.getElementById("italic").classList.remove("white-color");
    setItalic(true);
  };
  const addStrike = () => {
    document.getElementById("innerText").classList.add("strike");
    document.getElementById("strike").classList.add("white-color");
    setStrike(false);
  };

  const removeStrike = () => {
    document.getElementById("innerText").classList.remove("strike");
    document.getElementById("strike").classList.remove("white-color");
    setStrike(true);
  };

  const selectFile = (e) => {
    setCurrentMsg(e.target.files[0].name);
    setFile(e.target.files[0]);
  };

  const renderMsgs = (msgContent, index) => {
    if (msgContent.type === "file") {
      const blob = new Blob([msgContent.body], {
        type: msgContent.type,
      });
      return (
        <div
          className="msg"
          id={username === msgContent.author ? "you" : "other"}
          key={index}
        >
          <div>
            <div
              className="msg-content"
              style={{ background: "transparent" }}
            >
              <Image fileName={msgContent.fileName} blob={blob} />
            </div>
            <div className="msg-meta">
              <p id="time">{msgContent.time}</p>
              <p id="author">{msgContent.author}</p>
            </div>
          </div>
        </div>
      );
    } else
      return (
        <div
          className="msg"
          id={username === msgContent.author ? "you" : "other"}
          key={index}
        >
          <div>
            <div className="msg-content">
              <p>{msgContent.msg}</p>
            </div>
            <div className="msg-meta">
              <p id="time">{msgContent.time}</p>
              <p id="author">{msgContent.author}</p>
            </div>
          </div>
        </div>
      );
  };

  const value = {
    msgList,
    currentMsg,
    setCurrentMsg,
    sendMsg,
    bold,
    italic,
    strike,
    addBold,
    removeBold,
    addItalic,
    removeItalic,
    addStrike,
    removeStrike,
    selectFile,
    renderMsgs,
  };

  return <ChatBoxRenderer value={value} />;
};

export default ChatBoxContainer;
